mapper.insert:
insert into qg_db.qg_order ( userId, stockId,        orderNo,        goodsId,        num,        amount,        status,        aliTradeNo,        createdTime,        updatedTime        )
values
(#{userId},#{stockId},#{orderNo},#{goodsId},#{num},#{amount}, #{status},#{aliTradeNo},#{createdTime},#{updatedTime} );

mapper.select:
select 	id,userId,stockId,orderNo,goodsId,num,amount,status,aliTradeNo,createdTime, updatedTime
from qg_order

    userId=#{userId}
    orderNo=#{orderNo}
    stockId=#{stockId}
    aliTradeNo=#{aliTradeNo}
    status=#{status}

order by createdTime desc
limit 
        #{from}
        0
    ,

        #{size}
        5

mapper.update:
update qg_db.qg_order

    status = #{status} ,
    aliTradeNo = #{aliTradeNo} ,
    updatedTime = #{updatedTime}

where id = #{id}
